import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-operandos',
  templateUrl: './operandos.component.html',
})
export class OperandosComponent {

  @Output() resultChanged = new EventEmitter<string>();

  numeroUno;
  numeroDos:number;

  sumar(): void {
    let primero = parseInt(this.numeroUno);
    let segundo = parseInt(this.numeroDos);
    let resultado = primero + segundo;
    this.resultChanged.emit(resultado.toString());
    this.numeroUno = '';
    this.numeroDos = '';
  }
}
<div class="form-floating mb-3">
  <input
    type="number"
    class="form-control mt-4"
    id="numeroUno"
    placeholder="Primer digito"
    [(ngModel)]="numeroUno"
  />
  <label for="numeroUno">Ingrese el primer digito:</label>
</div>
<div class="form-floating">
  <input
    type="number"
    class="form-control mt-4"
    id="numeroDos"
    placeholder="Segundo digito"
    [(ngModel)]="numeroDos"
  />
  <label for="numeroDos">Ingrese el segundo digito:</label>
</div>

<button class="btn btn-primary mt-5" (click)="sumar()">Sumar</button>
