import { Component } from '@angular/core';

@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
})
export class CalculadoraComponent {

  numerUno: number = 1;
  numeroDos: number = 1;

  result: number = 2;

  sumar():number {

  }
}
<div class="container">

  <div class="form-floating mb-3">
    <input type="email" class="form-control mt-4" id="floatingInput" placeholder="name@example.com">
    <label for="floatingInput">Ingrese el primer digito:</label>
  </div>
  <div class="form-floating">
    <input type="password" class="form-control mt-4" id="floatingPassword" placeholder="Password">
    <label for="floatingPassword">Ingrese el segundo digito:</label>
  </div>

  <button class="btn btn-primary mt-5">Sumar</button>

  <p class="mt-5">Resultado: {{ result }}</p>
</div>
