import { Component } from '@angular/core';

@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
})
export class CalculadoraComponent {

  numeroUno = '';
  numeroDos = '';

  result:string = '';

  sumar(): void {
    let primero = parseInt(this.numeroUno);
    let segundo = parseInt(this.numeroDos);
    let resultado = primero + segundo
    this.result = resultado.toString();
    console.log(resultado);
    // if (primero === 0) {
    //   alert('Error, por favor intente de nuevo...')
    // }
    this.numeroUno = '';
    this.numeroDos = '';
  }
}
<div class="container">
  <div class="form-floating mb-3">
    <input
      type="number"
      class="form-control mt-4"
      id="numeroUno"
      placeholder="Primer digito"
      [(ngModel)]="numeroUno"
    />
    <label for="numeroUno">Ingrese el primer digito:</label>
  </div>
  <div class="form-floating">
    <input
      type="number"
      class="form-control mt-4"
      id="numeroDos"
      placeholder="Segundo digito"
      [(ngModel)]="numeroDos"
    />
    <label for="numeroDos">Ingrese el segundo digito:</label>
  </div>

  <button class="btn btn-primary mt-5" (click)="sumar()">Sumar</button>

  <p class="mt-5 alert alert-success">Resultado: {{ result }}</p>
</div>
