import { Component } from '@angular/core';

@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
})
export class CalculadoraComponent {

  numeroUno = 0;
  numeroDos:number = 0;

  result = '';

  sumar(numeroUno: number, numeroDos: number) {
    let resultado = numeroUno + numeroDos
    this.result = resultado.toString();
    console.log(resultado);
  }
}
<div class="container">

  <div class="form-floating mb-3">
    <input type="email" class="form-control mt-4" id="numeroUno" placeholder="name@example.com">
    <label for="numeroUno">Ingrese el primer digito:</label>
  </div>
  <div class="form-floating">
    <input type="password" class="form-control mt-4" id="numeroDos" placeholder="Password">
    <label for="numeroDos">Ingrese el segundo digito:</label>
  </div>

  <button class="btn btn-primary mt-5" (click)="sumar(2,2)">Sumar</button>

  <p class="mt-5">Resultado: {{ result }}</p>
</div>
