import { Component } from '@angular/core';

@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
})
export class CalculadoraComponent {
  result:string = '';

  modificarResutado(resultado: string): void{
    this.result = resultado;
  }

}
<div class="container">
  <h1>Apliacion de calculadora para sumar dos numeros</h1>

  <!-- Modulo de operandos -->
  <app-operandos (resultChanged)="modificarResultado($e)">

  </app-operandos>


  <!-- Modulo de resultado -->
  <app-resultado [resultado]="result">
  </app-resultado>
</div>
