import { Component } from "@angular/core";

@Component({
  selector: 'app-personas',
  templateUrl: './personas.component.html',
  styleUrls: ['./personas.component.css']
})

export class PeopleComponent{
  deshabilitar:boolean = false;
  mensaje:string = 'No se ha agregado ninguna persona';
  titulo = '';

  mostrar: boolean = false;

  agregarPersona(): void {
    this.mostrar = true;
    this.mensaje = 'Persona agregada';
  }

  // Metodo para modificar usando event binding
  // modificarTitulo(event:Event):void {
  //   const title = (<HTMLInputElement>event.target).value;
  //   this.titulo = title;
  //   console.log('Se esta ejecutando el metodo modificar titulo');
  //   console.log(title);
  // }


}
<div class="container">
  <div class="row">
    <div class="col">
      <h3>Listado de personas</h3>
      <button
        class="btn btn-primary"
        [disabled]="deshabilitar"
        (click)="agregarPersona()"
      >
        Agregar persona
      </button>

      <p *ngIf="mostrar">{{ mensaje }} con el titulo de {{ titulo }}</p>
      

      <app-persona></app-persona>
      <label for="titulo">Titulo personas</label>main
      <input
        type="text"
        name="titulo"
        id="titulo"
        class="form-control"
        [(ngModel)]="titulo"
      />
      {{ titulo }}
    </div>
  </div>
</div>

