@Component({
  selector: 'app-personas',
  templateUrl: './personas.component.html',
  styleUrls: ['./personas.component.css']
})

export class PeopleComponent{
  deshabilitar:boolean = false;
  mensaje:string = 'No se ha agregado ninguna persona';
